package week1.day1;

public class LearnIFCondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number =55;
		if(number>0) {
			System.out.println("The given number is " +number+" positive");
		}
		else if(number<0){
			System.out.println("The given number is "+number+" negative");	
			
		}
		else {
			System.out.println("The given number is "+number+" netural");
		}
	}

}
